pattern = 'CGTATGTGATCA'
text = ''
d = 4

indexes = (0..text.size-1).select { |i|
                              s = text[i..i+pattern.size-1].split(//)
                              diff = pattern.split(//).zip(s).count { |a,b| a!=b }
                              diff <= d
                          }

puts indexes.join(' ')